The following code shows the constructor with several compatible types. #include<json.hpp>
#include<deque>
#include<list>
#include<forward_list>
#include<set>
#include<unordered_map>
#include<unordered_set>

usingjson=nlohmann::json;

intmain()
{
//============
//objecttypes
//============

//createanobjectfromanobject_tvalue
json::object_tobject_value={{"one",1},{"two",2}};
jsonj_object_t(object_value);

//createanobjectfromstd::map
std::map<std::string,int>c_map
{
{"one",1},{"two",2},{"three",3}
};
jsonj_map(c_map);

//createanobjectfromstd::unordered_map
std::unordered_map<constchar*,double>c_umap
{
{"one",1.2},{"two",2.3},{"three",3.4}
};
jsonj_umap(c_umap);

//createanobjectfromstd::multimap
std::multimap<std::string,bool>c_mmap
{
{"one",true},{"two",true},{"three",false},{"three",true}
};
jsonj_mmap(c_mmap);//onlyoneentryforkey"three"isused

//createanobjectfromstd::unordered_multimap
std::unordered_multimap<std::string,bool>c_ummap
{
{"one",true},{"two",true},{"three",false},{"three",true}
};
jsonj_ummap(c_ummap);//onlyoneentryforkey"three"isused

//serializetheJSONobjects
std::cout<<j_object_t<<'\n';
std::cout<<j_map<<'\n';
std::cout<<j_umap<<'\n';
std::cout<<j_mmap<<'\n';
std::cout<<j_ummap<<"\n\n";


//===========
//arraytypes
//===========

//createanarrayfromanarray_tvalue
json::array_tarray_value={"one","two",3,4.5,false};
jsonj_array_t(array_value);

//createanarrayfromstd::vector
std::vector<int>c_vector{1,2,3,4};
jsonj_vec(c_vector);

//createanarrayfromstd::deque
std::deque<double>c_deque{1.2,2.3,3.4,5.6};
jsonj_deque(c_deque);

//createanarrayfromstd::list
std::list<bool>c_list{true,true,false,true};
jsonj_list(c_list);

//createanarrayfromstd::forward_list
std::forward_list<int64_t>c_flist{12345678909876,23456789098765,34567890987654,45678909876543};
jsonj_flist(c_flist);

//createanarrayfromstd::array
std::array<unsignedlong,4>c_array{{1,2,3,4}};
jsonj_array(c_array);

//createanarrayfromstd::set
std::set<std::string>c_set{"one","two","three","four","one"};
jsonj_set(c_set);//onlyoneentryfor"one"isused

//createanarrayfromstd::unordered_set
std::unordered_set<std::string>c_uset{"one","two","three","four","one"};
jsonj_uset(c_uset);//onlyoneentryfor"one"isused

//createanarrayfromstd::multiset
std::multiset<std::string>c_mset{"one","two","one","four"};
jsonj_mset(c_mset);//bothentriesfor"one"areused

//createanarrayfromstd::unordered_multiset
std::unordered_multiset<std::string>c_umset{"one","two","one","four"};
jsonj_umset(c_umset);//bothentriesfor"one"areused

//serializetheJSONarrays
std::cout<<j_array_t<<'\n';
std::cout<<j_vec<<'\n';
std::cout<<j_deque<<'\n';
std::cout<<j_list<<'\n';
std::cout<<j_flist<<'\n';
std::cout<<j_array<<'\n';
std::cout<<j_set<<'\n';
std::cout<<j_uset<<'\n';
std::cout<<j_mset<<'\n';
std::cout<<j_umset<<"\n\n";


//============
//stringtypes
//============

//createstringfromastring_tvalue
json::string_tstring_value="Thequickbrownfoxjumpsoverthelazydog.";
jsonj_string_t(string_value);

//createaJSONstringdirectlyfromastringliteral
jsonj_string_literal("Thequickbrownfoxjumpsoverthelazydog.");

//createstringfromstd::string
std::strings_stdstring="Thequickbrownfoxjumpsoverthelazydog.";
jsonj_stdstring(s_stdstring);

//serializetheJSONstrings
std::cout<<j_string_t<<'\n';
std::cout<<j_string_literal<<'\n';
std::cout<<j_stdstring<<"\n\n";


//============
//numbertypes
//============

//createaJSONnumberfromnumber_integer_t
json::number_integer_tvalue_integer_t=-42;
jsonj_integer_t(value_integer_t);

//createaJSONnumberfromnumber_unsigned_t
json::number_integer_tvalue_unsigned_t=17;
jsonj_unsigned_t(value_unsigned_t);

//createaJSONnumberfromananonymousenum
enum{enum_value=17};
jsonj_enum(enum_value);

//createvaluesofdifferentintegertypes
shortn_short=42;
intn_int=-23;
longn_long=1024;
int_least32_tn_int_least32_t=-17;
uint8_tn_uint8_t=8;

//create(integer)JSONnumbers
jsonj_short(n_short);
jsonj_int(n_int);
jsonj_long(n_long);
jsonj_int_least32_t(n_int_least32_t);
jsonj_uint8_t(n_uint8_t);

//createvaluesofdifferentfloating-pointtypes
json::number_float_tv_ok=3.141592653589793;
json::number_float_tv_nan=NAN;
json::number_float_tv_infinity=INFINITY;

//createvaluesofdifferentfloating-pointtypes
floatn_float=42.23;
floatn_float_nan=1.0f/0.0f;
doublen_double=23.42;

//create(floatingpoint)JSONnumbers
jsonj_ok(v_ok);
jsonj_nan(v_nan);
jsonj_infinity(v_infinity);
jsonj_float(n_float);
jsonj_float_nan(n_float_nan);
jsonj_double(n_double);

//serializetheJSONnumbers
std::cout<<j_integer_t<<'\n';
std::cout<<j_unsigned_t<<'\n';
std::cout<<j_enum<<'\n';
std::cout<<j_short<<'\n';
std::cout<<j_int<<'\n';
std::cout<<j_long<<'\n';
std::cout<<j_int_least32_t<<'\n';
std::cout<<j_uint8_t<<'\n';
std::cout<<j_ok<<'\n';
std::cout<<j_nan<<'\n';
std::cout<<j_infinity<<'\n';
std::cout<<j_float<<'\n';
std::cout<<j_float_nan<<'\n';
std::cout<<j_double<<"\n\n";


//=============
//booleantypes
//=============

//createbooleanvalues
jsonj_truth=true;
jsonj_falsity=false;

//serializetheJSONbooleans
std::cout<<j_truth<<'\n';
std::cout<<j_falsity<<'\n';
}
 Output (play with this example <a target="_blank" href="http://melpon.org/wandbox/permlink/VM7W2kpE7sIYJ5DW"><b>online</b></a>): {"one":1,"two":2}
