The example shows several calls to get_ref(). #include<json.hpp>

usingjson=nlohmann::json;

intmain()
{
//createaJSONnumber
jsonvalue=17;

//explicitlygettingreferences
autor1=value.get_ref<constjson::number_integer_t&>();
autor2=value.get_ref<json::number_integer_t&>();

//printthevalues
std::cout<<r1<<''<<r2<<'\n';

//incompatibletypethrowsexception
try
{
autor3=value.get_ref<json::number_float_t&>();
}
catch(json::type_error&ex)
{
std::cout<<ex.what()<<'\n';
}
}
