The example below shows how array elements can be read and written using at(). It also demonstrates the different exceptions that can be thrown. #include<json.hpp>

usingjson=nlohmann::json;

intmain()
{
//createJSONarray
jsonarray={"first","2nd","third","fourth"};

//outputelementatindex2(thirdelement)
std::cout<<array.at(2)<<'\n';

//changeelementatindex1(secondelement)to"second"
array.at(1)="second";

//outputchangedarray
std::cout<<array<<'\n';


//exceptiontype_error.304
try
{
//useat()onanon-arraytype
jsonstr="Iamastring";
str.at(0)="Anotherstring";
}
catch(json::type_error&e)
{
std::cout<<e.what()<<'\n';
}

//exceptionout_of_range.401
try
{
//trytowritebeyondthearraylimit
array.at(5)="sixth";
}
catch(json::out_of_range&e)
{
std::cout<<e.what()<<'\n';
}
}
