The example below shows how object elements can be read and written using the [] operator. #include<json.hpp>

usingjson=nlohmann::json;

intmain()
{
//createaJSONobject
jsonobject=
{
{"one",1},{"two",2},{"three",2.9}
};

//outputelementwithkey"two"
std::cout<<object["two"]<<"\n\n";

//changeelementwithkey"three"
object["three"]=3;

//outputchangedarray
std::cout<<std::setw(4)<<object<<"\n\n";

//mentionnonexistingkey
object["four"];

//writetononexistingkey
object["five"]["really"]["nested"]=true;

//outputchangedobject
std::cout<<std::setw(4)<<object<<'\n';
}
