The example shows how find() is used. #include<json.hpp>

usingjson=nlohmann::json;

intmain()
{
//createaJSONobject
jsonj_object={{"one",1},{"two",2}};

//callfind
autoit_two=j_object.find("two");
autoit_three=j_object.find("three");

//printvalues
std::cout<<std::boolalpha;
std::cout<<"\"two\"wasfound:"<<(it_two!=j_object.end())<<'\n';
std::cout<<"valueatkey\"two\":"<<*it_two<<'\n';
std::cout<<"\"three\"wasfound:"<<(it_three!=j_object.end())<<'\n';
}
