The example below shows the serialization with different parameters to width to adjust the indentation level. #include<json.hpp>

usingjson=nlohmann::json;

intmain()
{
//createJSONvalues
jsonj_object={{"one",1},{"two",2}};
jsonj_array={1,2,4,8,16};

//serializewithoutindentation
std::cout<<j_object<<"\n\n";
std::cout<<j_array<<"\n\n";

//serializewithindentation
std::cout<<std::setw(4)<<j_object<<"\n\n";
std::cout<<std::setw(2)<<j_array<<"\n\n";
}
