The example below demonstrates the parse() function with and without callback function. #include<json.hpp>

usingjson=nlohmann::json;

intmain()
{
//aJSONtext
autotext=R"(
{
"Image":{
"Width":800,
"Height":600,
"Title":"Viewfrom15thFloor",
"Thumbnail":{
"Url":"http://www.example.com/image/481989943",
"Height":125,
"Width":100
},
"Animated":false,
"IDs":[116,943,234,38793]
}
}
)";

//parseandserializeJSON
jsonj_complete=json::parse(text);
std::cout<<std::setw(4)<<j_complete<<"\n\n";


//defineparsercallback
json::parser_callback_tcb=[](intdepth,json::parse_event_tevent,json&parsed)
{
//skipobjectelementswithkey"Thumbnail"
if(event==json::parse_event_t::keyandparsed==json("Thumbnail"))
{
returnfalse;
}
else
{
returntrue;
}
};

//parse(withcallback)andserializeJSON
jsonj_filtered=json::parse(text,cb);
std::cout<<std::setw(4)<<j_filtered<<'\n';
}
 Output (play with this example <a target="_blank" href="http://melpon.org/wandbox/permlink/n888UNQlMFduURhE"><b>online</b></a>): {
    "Image": {
        "Animated": false,
        "Height": 600,
        "IDs": [
            116,
            943,
            234,
            38793
        ],
        "Thumbnail": {
            "Height": 125,
            "Url": "http://www.example.com/image/481989943",
            "Width": 100
        },
        "Title": "View from 15th Floor",
        "Width": 800
    }
}

