The behavior is shown in the example. #include<json.hpp>

usingjson=nlohmann::json;

intmain()
{
//createaJSONvalue
jsonj=
{
{"number",1},{"string","foo"},{"array",{1,2}}
};

//read-onlyaccess

//outputelementwithJSONpointer"/number"
std::cout<<j.at("/number"_json_pointer)<<'\n';
//outputelementwithJSONpointer"/string"
std::cout<<j.at("/string"_json_pointer)<<'\n';
//outputelementwithJSONpointer"/array"
std::cout<<j.at("/array"_json_pointer)<<'\n';
//outputelementwithJSONpointer"/array/1"
std::cout<<j.at("/array/1"_json_pointer)<<'\n';

//writingaccess

//changethestring
j.at("/string"_json_pointer)="bar";
//outputthechangedstring
std::cout<<j["string"]<<'\n';

//changeanarrayelement
j.at("/array/1"_json_pointer)=21;
//outputthechangedarray
std::cout<<j["array"]<<'\n';


//out_of_range.106
try
{
//trytouseanarrayindexwithleading'0'
json::referenceref=j.at("/array/01"_json_pointer);
}
catch(json::parse_error&e)
{
std::cout<<e.what()<<'\n';
}

//out_of_range.109
try
{
//trytouseanarrayindexthatisnotanumber
json::referenceref=j.at("/array/one"_json_pointer);
}
catch(json::parse_error&e)
{
std::cout<<e.what()<<'\n';
}

//out_of_range.401
try
{
//trytouseaaninvalidarrayindex
json::referenceref=j.at("/array/4"_json_pointer);
}
catch(json::out_of_range&e)
{
std::cout<<e.what()<<'\n';
}

//out_of_range.402
try
{
//trytousethearrayindex'-'
json::referenceref=j.at("/array/-"_json_pointer);
}
catch(json::out_of_range&e)
{
std::cout<<e.what()<<'\n';
}

//out_of_range.404
try
{
//trytouseaJSONpointerthatcannotberesolved
json::referenceref=j.at("/number/foo"_json_pointer);
}
catch(json::out_of_range&e)
{
std::cout<<e.what()<<'\n';
}
}
 Output (play with this example <a target="_blank" href="http://melpon.org/wandbox/permlink/Fy2xBfZMols2DUQC"><b>online</b></a>): 1
"foo"
[1,2]
2
"bar"
[1,21]
[json.exception.parse_error.106] parse error: array index '01' must not begin with '0'
[json.exception.parse_error.109] parse error: array index 'one' is not a number
[json.exception.out_of_range.401] array index 4 is out of range
[json.exception.out_of_range.402] array index '-' (2) is out of range
[json.exception.out_of_range.404] unresolved reference token 'foo'
 The example code above can be translated withg++ -std=c++11 -Isrc doc/examples/at_json_pointer.cpp -o at_json_pointer

