The following code exemplifies type_name() for all JSON types. #include<json.hpp>

usingjson=nlohmann::json;

intmain()
{
//createJSONvalues
jsonj_null;
jsonj_boolean=true;
jsonj_number_integer=17;
jsonj_number_float=23.42;
jsonj_object={{"one",1},{"two",2}};
jsonj_array={1,2,4,8,16};
jsonj_string="Hello,world";

//calltype_name()
std::cout<<j_null.type_name()<<'\n';
std::cout<<j_boolean.type_name()<<'\n';
std::cout<<j_number_integer.type_name()<<'\n';
std::cout<<j_number_float.type_name()<<'\n';
std::cout<<j_object.type_name()<<'\n';
std::cout<<j_array.type_name()<<'\n';
std::cout<<j_string.type_name()<<'\n';
}
